    def updateRole(self):
        id = self.lineUpdateAccountId.text()
        role = self.lineUpdateRole.text()
        employee = Select.select_employee(id)
        if employee:
            cursor.execute(sql_queries.select_jobs)
            jobs = cursor.fetchall()
            result = [[tuple[1][0],tuple[1][1]] for tuple in enumerate(jobs) if tuple[1][1].lower()==role.lower()]
            if result:
                cursor.execute(sql_queries.admin_update_emp,(result[0][0],id))
                connection.commit()
                self.lineUpdateAccountId.clear()
                self.lineUpdateRole.clear()
            else:
                msg = QtWidgets.QMessageBox()
                msg.setText("Role must be : AccessAdmin-Admin-DataWriter-DataReader-Saler-SalerManager!")
                msg.setIcon(QtWidgets.QMessageBox.Information)
                msg.setWindowTitle("Warning")
                msg.exec_()
        else:
            msg = QtWidgets.QMessageBox()
            msg.setText("Account doesn't exist!!")
            msg.setIcon(QtWidgets.QMessageBox.Information)
            msg.setWindowTitle("Warning")
            msg.exec_()


    def updatePassword(self):
        id = self.lineUpdateId.text()
        password = self.lineUpdatePassword.text()
        account = Select.select_account(id)
        if account:
            if len(password)>7:
                cursor.execute(sql_queries.update_account,(password,id))
                connection.commit()
                self.lineUpdatePassword.clear()
                self.lineUpdateId.clear()
            else:
                msg = QtWidgets.QMessageBox()
                msg.setText("Password must be at least 8 characters")
                msg.setIcon(QtWidgets.QMessageBox.Information)
                msg.setWindowTitle("Warning")
                msg.exec_()
        else:
            msg = QtWidgets.QMessageBox()
            msg.setText("Account doesn't exist!!")
            msg.setIcon(QtWidgets.QMessageBox.Information)
            msg.setWindowTitle("Warning")
            msg.exec_()


    def getAccount(self):
        id = 0
        row = 0
        if self.stackedWidget.currentWidget() == self.pageGetAccount:
            id = self.lineAccountId.text()
            account = Select.select_account(id)
            employee = Select.select_employee(id)
            if account:
                self.lineAccountId.clear()
                self.tableAccount.insertRow(0)
                self.tableAccount.setItem(row, 0, QtWidgets.QTableWidgetItem(str(account.id)))
                self.tableAccount.setItem(row, 1, QtWidgets.QTableWidgetItem(account.password))
                self.tableAccount.setItem(row, 2, QtWidgets.QTableWidgetItem(employee.first_name))
                self.tableAccount.setItem(row, 3, QtWidgets.QTableWidgetItem(employee.last_name))
                self.tableAccount.setItem(row, 4, QtWidgets.QTableWidgetItem(employee.job))
                self.tableAccount.setItem(row, 5, QtWidgets.QTableWidgetItem(str(employee.salary)))
        elif self.stackedWidget.currentWidget() == self.pageUpdatePassword:
            id = self.lineCheckAccountId.text()
            account = Select.select_account(id)
            employee = Select.select_employee(id)
            if account:
                self.lineCheckAccountId.clear()
                self.tableUpdatePassword.setRowCount(0)
                self.tableUpdatePassword.insertRow(0)
                self.tableUpdatePassword.setItem(row, 0, QtWidgets.QTableWidgetItem(str(account.id)))
                self.tableUpdatePassword.setItem(row, 1, QtWidgets.QTableWidgetItem(account.password))
                self.tableUpdatePassword.setItem(row, 2, QtWidgets.QTableWidgetItem(employee.first_name))
                self.tableUpdatePassword.setItem(row, 3, QtWidgets.QTableWidgetItem(employee.last_name))
                self.tableUpdatePassword.setItem(row, 4, QtWidgets.QTableWidgetItem(employee.job))
                self.tableUpdatePassword.setItem(row, 5, QtWidgets.QTableWidgetItem(str(employee.salary)))
        else:
            id = self.lineCheckRoleId.text()
            account = Select.select_account(id)
            employee = Select.select_employee(id)
            if account:
                self.lineCheckRoleId.clear()
                self.tableUpdateRole.setRowCount(0)
                self.tableUpdateRole.insertRow(0)
                self.tableUpdateRole.setItem(row, 0, QtWidgets.QTableWidgetItem(str(account.id)))
                self.tableUpdateRole.setItem(row, 1, QtWidgets.QTableWidgetItem(account.password))
                self.tableUpdateRole.setItem(row, 2, QtWidgets.QTableWidgetItem(employee.first_name))
                self.tableUpdateRole.setItem(row, 3, QtWidgets.QTableWidgetItem(employee.last_name))
                self.tableUpdateRole.setItem(row, 4, QtWidgets.QTableWidgetItem(employee.job))
                self.tableUpdateRole.setItem(row, 5, QtWidgets.QTableWidgetItem(str(employee.salary)))


    def getAccounts(self):
        self.tableAccounts.setRowCount(0)
        accounts = Select.select_accounts()
        employees = Select.select_employees()
        row = 0
        for i in range(len(accounts)):
            self.tableAccounts.insertRow(row)
            self.tableAccounts.setItem(row, 0, QtWidgets.QTableWidgetItem(str(accounts[i].id)))
            self.tableAccounts.setItem(row, 1, QtWidgets.QTableWidgetItem(accounts[i].password))
            self.tableAccounts.setItem(row, 2, QtWidgets.QTableWidgetItem(employees[i].first_name))
            self.tableAccounts.setItem(row, 3, QtWidgets.QTableWidgetItem(employees[i].last_name))
            self.tableAccounts.setItem(row, 4, QtWidgets.QTableWidgetItem(employees[i].job))
            self.tableAccounts.setItem(row, 5, QtWidgets.QTableWidgetItem(str(employees[i].salary)))
            row = row+1



