    def getCustomers(self):
        self.tableCustomers.setRowCount(0)
        customers = Select.select_customers()
        i = 0
        for customer in customers:
            self.tableCustomers.insertRow(i)
            self.tableCustomers.setItem(i, 0, QtWidgets.QTableWidgetItem(str(customer.id)))
            self.tableCustomers.setItem(i, 1,
                                        QtWidgets.QTableWidgetItem(customer.first_name + " " + customer.last_name))
            self.tableCustomers.setItem(i, 2, QtWidgets.QTableWidgetItem(customer.phone))
            self.tableCustomers.setItem(i, 3, QtWidgets.QTableWidgetItem(customer.address))
            i = i + 1

    def getCustomer(self):
        id = self.lineCustomerId.text()
        if id:
            id = int(id)
            self.lineCustomerId.clear()
            if id > 0:
                customer = Select.select_customer(id)
                if customer:
                    self.tableCustomer.insertRow(0)
                    self.tableCustomer.setItem(0, 0, QtWidgets.QTableWidgetItem(str(id)))
                    self.tableCustomer.setItem(0, 1,
                                               QtWidgets.QTableWidgetItem(
                                                   customer.first_name + " " + customer.last_name))
                    self.tableCustomer.setItem(0, 2, QtWidgets.QTableWidgetItem(customer.phone))
                    self.tableCustomer.setItem(0, 3, QtWidgets.QTableWidgetItem(customer.address))

    def getProducts(self):
        lowerBound = self.lineLower.text()
        upperBound = self.lineUpper.text()
        row = 0
        self.tableProducts.setRowCount(0)
        if upperBound:
            upperBound = int(upperBound)
            if lowerBound:
                lowerBound = int(lowerBound)
            else:
                lowerBound = 1
            for i in range(lowerBound,upperBound+1):
                product = Select.select_product(i)
                category = Select.select_category(product.category)
                subcategory = Select.select_subcategory(product.category, product.subcategory)
                image = Select.getImage(product.image)
                self.tableProducts.insertRow(row)
                self.tableProducts.setItem(row, 0, QtWidgets.QTableWidgetItem(str(product.id)))
                self.tableProducts.setCellWidget(row, 1, image)
                self.tableProducts.setItem(row, 2, QtWidgets.QTableWidgetItem(product.name))
                self.tableProducts.setItem(row, 3, QtWidgets.QTableWidgetItem(str(product.price)))
                self.tableProducts.setItem(row, 4, QtWidgets.QTableWidgetItem(str(product.stock)))
                self.tableProducts.setItem(row, 5, QtWidgets.QTableWidgetItem(category))
                self.tableProducts.setItem(row, 6, QtWidgets.QTableWidgetItem(subcategory))
                row = row + 1
        elif lowerBound:
            lowerBound = int(lowerBound)
            for i in range(lowerBound, self.maxId+1):
                product = Select.select_product(i)
                category = Select.select_category(product.category)
                subcategory = Select.select_subcategory(product.category, product.subcategory)
                image = Select.getImage(product.image)
                self.tableProducts.insertRow(row)
                self.tableProducts.setItem(row, 0, QtWidgets.QTableWidgetItem(str(product.id)))
                self.tableProducts.setCellWidget(row, 1, image)
                self.tableProducts.setItem(row, 2, QtWidgets.QTableWidgetItem(product.name))
                self.tableProducts.setItem(row, 3, QtWidgets.QTableWidgetItem(str(product.price)))
                self.tableProducts.setItem(row, 4, QtWidgets.QTableWidgetItem(str(product.stock)))
                self.tableProducts.setItem(row, 5, QtWidgets.QTableWidgetItem(category))
                self.tableProducts.setItem(row, 6, QtWidgets.QTableWidgetItem(subcategory))
                row = row + 1
        else:
            if self.clickedBtnGetProductsPage == False:
                self.clickedBtnGetProductsPage = True
                self.showWarning()
            else:
                for i in range(1, self.maxId+1):
                    product = Select.select_product(i)
                    category = Select.select_category(product.category)
                    subcategory = Select.select_subcategory(product.category, product.subcategory)
                    image = Select.getImage(product.image)
                    self.tableProducts.insertRow(row)
                    self.tableProducts.setItem(row, 0, QtWidgets.QTableWidgetItem(str(product.id)))
                    self.tableProducts.setCellWidget(row, 1, image)
                    self.tableProducts.setItem(row, 2, QtWidgets.QTableWidgetItem(product.name))
                    self.tableProducts.setItem(row, 3, QtWidgets.QTableWidgetItem(str(product.price)))
                    self.tableProducts.setItem(row, 4, QtWidgets.QTableWidgetItem(str(product.stock)))
                    self.tableProducts.setItem(row, 5, QtWidgets.QTableWidgetItem(category))
                    self.tableProducts.setItem(row, 6, QtWidgets.QTableWidgetItem(subcategory))
                    row = row + 1

    def showWarning(self):
        msg = QtWidgets.QMessageBox()
        msg.setWindowTitle("Warning!")
        msg.setText("It can take a very long time to view all the products. Therefore, it would be healthier to set a range.")
        msg.setIcon(QtWidgets.QMessageBox.Warning)
        msg.setStandardButtons(QtWidgets.QMessageBox.Ok)
        msg.setStyleSheet("font-size:20px;color:red;")
        msg.exec_()

