    def addProduct(self):
        self.lblInfoAddProduct.setText("")
        name = self.lineName.text()
        price = self.linePrice.text()
        stock = self.lineStock.text()
        category = self.lineCategory.text()
        subcategory = self.lineSubCategory.text()
        cursor.execute(sql_queries.select_max_category)
        maxCategory = cursor.fetchone()[0]
        if ((not name) | (not price) | (not stock) | (not category) | (not subcategory)):
            self.lblInfoAddProduct.setStyleSheet("margin-top:20px;margin-right:40px;color:red;")
            self.lblInfoAddProduct.setText("empty lines!!")
        elif (int(category) > 0) & (int(category) <= maxCategory) & (self.image!=None):
            category = int(category)
            subcategory = int(subcategory)
            cursor.execute(sql_queries.select_max_subcategory, (category,))
            maxSubcategory = cursor.fetchone()[0]
            if (subcategory > 0) & (subcategory <= maxSubcategory):
                if (len(name) > 3) & (float(price) > 0) & (int(stock) >= 0) & (int(category) > 0) & (
                        int(subcategory) > 0):
                    cursor.execute(sql_queries.insert_product, (name, price, stock, category, subcategory, self.image))
                    connection.commit()
                    self.lblInfoAddProduct.setStyleSheet("margin-top:20px;margin-right:40px;color:green;")
                    self.lblInfoAddProduct.setText("Product Added")
                    self.image = None
                    self.lineName.clear()
                    self.linePrice.clear()
                    self.lineStock.clear()
                    self.lineCategory.clear()
                    self.lineSubCategory.clear()

                else:
                    self.lblInfoAddProduct.setStyleSheet("margin-top:20px;margin-right:40px;color:red;")
                    self.lblInfoAddProduct.setText(
                        "Requirements : Name > 3, Price > 0, Stock >= 0, Category > 0, Subcategory > 0")
            else:
                self.lblInfoAddProduct.setStyleSheet("margin-top:20px;margin-right:40px;color:red;")
                self.lblInfoAddProduct.setText("Category or SubCategory must be valid. Check with show buttons!!")

    def updateProduct(self):
        id = self.lineUpdateId.text()
        product = Select.select_product(id)
        if product:
            name = self.lineUpdateName.text() or product.name
            price = self.lineUpdatePrice.text() or product.price
            stock = self.lineUpdateStock.text() or product.stock
            category = self.lineUpdateCategory.text() or product.category
            subcategory = self.lineUpdateSubCategory.text() or product.subcategory
            if self.updateImage == None:
                self.updateImage = product.image
            cursor.execute(sql_queries.update_product, (name, price, stock, category, subcategory, self.updateImage,id))
            connection.commit()
            self.updateImage = None
        self.lineUpdateId.clear()
        self.lineUpdateName.clear()
        self.lineUpdatePrice.clear()
        self.lineUpdateStock.clear()
        self.lineUpdateCategory.clear()
        self.lineUpdateSubCategory.clear()

    def addCustomer(self):
        self.lblInfoAddCustomer.setText("")
        first_name = self.lineCustomerFirstName.text()
        last_name = self.lineCustomerLastName.text()
        phone = self.lineCustomerPhone.text()
        address = self.lineCustomerAddress.text()
        if (len(first_name) > 3) & (len(last_name) > 3) & (len(phone) > 10) & (len(address) > 10):
            cursor.execute(sql_queries.insert_customer, (first_name, last_name, phone, address))
            connection.commit()
            self.lblInfoAddCustomer.setText("Customer Added")
            self.lineCustomerFirstName.clear()
            self.lineCustomerAddress.clear()
            self.lineCustomerLastName.clear()
            self.lineCustomerPhone.clear()
        else:
            self.lblInfoAddCustomer.setText("Minimum Lengths : First Name = 3, Last Name = 3, Phone = 10, Address = 10")

    def getProduct(self):
        self.tableUpdateProduct.setRowCount(0)
        id = self.lineCheckProductId.text()
        self.lineCheckProductId.clear()
        if id:
            id = int(id)
            product = Select.select_product(id)
            if product:
                image = Select.getImage(product.image)
                category = Select.select_category(product.category)
                subcategory = Select.select_subcategory(product.category, product.subcategory)
                self.tableUpdateProduct.insertRow(0)
                self.tableUpdateProduct.setItem(0, 0, QtWidgets.QTableWidgetItem(str(id)))
                self.tableUpdateProduct.setCellWidget(0, 1, image)
                self.tableUpdateProduct.setItem(0, 2, QtWidgets.QTableWidgetItem(product.name))
                self.tableUpdateProduct.setItem(0, 3, QtWidgets.QTableWidgetItem(str(product.stock)))
                self.tableUpdateProduct.setItem(0, 4, QtWidgets.QTableWidgetItem(str(product.price)))
                self.tableUpdateProduct.setItem(0, 5, QtWidgets.QTableWidgetItem(category))
                self.tableUpdateProduct.setItem(0, 6, QtWidgets.QTableWidgetItem(subcategory))


    def getCustomer(self):
        if self.stackedWidget.currentWidget() == self.pageDeleteCustomer:
            self.tableDeleteCustomer.setRowCount(0)
            id = int(self.lineCheckDeletedCustomerId.text())
            self.lineCheckDeletedCustomerId.clear()
            if id > 0:
                customer = Select.select_customer(id)
                if customer:
                    self.tableDeleteCustomer.insertRow(0)
                    self.tableDeleteCustomer.setItem(0, 0, QtWidgets.QTableWidgetItem(str(id)))
                    self.tableDeleteCustomer.setItem(0, 1, QtWidgets.QTableWidgetItem(customer.first_name))
                    self.tableDeleteCustomer.setItem(0, 2, QtWidgets.QTableWidgetItem(customer.last_name))
                    self.tableDeleteCustomer.setItem(0, 3, QtWidgets.QTableWidgetItem(customer.phone))
